import React from "react";
import { Button } from "reactstrap";

class Box extends React.Component {
  render() {
    return (
      <div className="border border box-container">
        <p>Your balance is: Rs.{this.props.value.toLocaleString("en-IN")}</p>
        <Button variant="dark" onClick={this.props.incBal}>
          + 10,000
        </Button>
        <Button variant="dark" onClick={this.props.decBal}>
          - 10,000
        </Button>
        <Button variant="dark" onClick={this.props.resetBal}>
          Donate to charity
        </Button>
      </div>
    );
  }
}

export default Box;
